import { createRequire as CreateRequire } from 'module'
import Babel from '@babel/core'
import Test from 'ava'

const Require = CreateRequire(import.meta.url)

Test.beforeEach((test) => {

  test.context.option = { 
    'plugins': [ 
      [
        Require.resolve('../../index.cjs'),
        {
          'rule': [
            {
              'searchFor': /^(\.{1,2}\/.*?)\.js$/gi,
              'replaceWith': '$1.cjs'
            }
          ]
        }      
      ]
    ]
  }

})

Test('import', async (test) => {

  let codeIn = 'import { abc } from \'./abc.js\''
  let expectedCodeOut = 'import { abc } from "./abc.cjs";'

  let { code: actualCodeOut } = await Babel.transformAsync(codeIn, test.context.option)

  test.is(actualCodeOut, expectedCodeOut)

})

Test('import(...)', async (test) => {

  let codeIn = 'const abcPromise = import(\'./abc.js\')'
  let expectedCodeOut = 'const abcPromise = import("./abc.cjs");'

  let { code: actualCodeOut } = await Babel.transformAsync(codeIn, test.context.option)

  test.is(actualCodeOut, expectedCodeOut)

})

Test('require(...)', async (test) => {

  let codeIn = 'const abc = require(\'./abc.js\')'
  let expectedCodeOut = 'const abc = require("./abc.cjs");'

  let { code: actualCodeOut } = await Babel.transformAsync(codeIn, test.context.option)

  test.is(actualCodeOut, expectedCodeOut)

})

Test('let', async (test) => {

  let codeIn = 'let abc = \'./abc.js\''
  let expectedCodeOut = 'let abc = "./abc.cjs";'

  let { code: actualCodeOut } = await Babel.transformAsync(codeIn, test.context.option)

  test.is(actualCodeOut, expectedCodeOut)

})

Test('let ...', async (test) => {

  let codeIn = 'let abc = \'./ab\' + \'c.js\''
  let expectedCodeOut = 'let abc = \'./ab\' + \'c.js\';'

  let { code: actualCodeOut } = await Babel.transformAsync(codeIn, test.context.option)

  test.is(actualCodeOut, expectedCodeOut)

})

Test('console.log(...)', async (test) => {

  let codeIn = 'console.log(\'Here at ./abc.js and there at ./def.js\')'
  let expectedCodeOut = 'console.log(\'Here at ./abc.js and there at ./def.js\');'

  let { code: actualCodeOut } = await Babel.transformAsync(codeIn, test.context.option)

  test.is(actualCodeOut, expectedCodeOut)

})

Test('fs.readFileAsync(...)', async (test) => {

  let codeIn = 'fs.readFileAsync(\'./abc.json\')'
  let expectedCodeOut = 'fs.readFileAsync(\'./abc.json\');'

  let { code: actualCodeOut } = await Babel.transformAsync(codeIn, test.context.option)

  test.is(actualCodeOut, expectedCodeOut)

})

import URL from 'url'

import { SourceMapSupport } from './index.cjs'

SourceMapSupport.install({ 
  'handleUncaughtExceptions': false, 
  'retrieveSourceMap': function(source) {

    if (/^file:/.test(source)) {
      return SourceMapSupport.retrieveSourceMap(URL.fileURLToPath(source))
    } else {
      return null
    }

  }
})

import { Plugin } from '@virtualpatterns/mablung-babel-plugin/plugin'

import { Visitor } from './visitor.cjs'

export default Plugin.createPlugin(Visitor)

import * as ChangeCase from 'change-case'

const { pascalCase: PascalCase } = ChangeCase

class Plugin {

  static createPlugin(visitorClass) {
    // console.log(`Plugin.createPlugin(${visitorClass.name})`)

    return function(babel) {
      // console.log(`Plugin.createPlugin(${visitorClass.name})(babel)`)

      let visitorInstance = new visitorClass(babel)

      let pluginObject = {}

      // pluginObject.manipulateOptions = function(option, parserOption) {
      //   console.dir(parserOption)
      // }

      pluginObject.pre = function(state) { visitorInstance.onBeforeNode(state) }
      pluginObject.post = function(state) { visitorInstance.onAfterNode(state) }

      let nodeType = visitorInstance.nodeType
      let visitorObject = {}

      nodeType.forEach((nodeType) => {

        let onNodeName = `on${PascalCase(nodeType)}Node`

        if (onNodeName in visitorInstance) {
          visitorObject[nodeType] = function(path, state) { visitorInstance[onNodeName](path, state, this) }
        }

        let onEnterNodeName = `onEnter${PascalCase(nodeType)}Node`
        let onExitNodeName = `onExit${PascalCase(nodeType)}Node`

        if (onEnterNodeName in visitorInstance ||
            onExitNodeName in visitorInstance) {

          visitorObject[nodeType] = { 

            enter(path, state) {
              if (onEnterNodeName in visitorInstance) {
                visitorInstance[onEnterNodeName](path, state, this)
              }
            }, 

            exit(path, state) {
              if (onExitNodeName in visitorInstance) {
                visitorInstance[onExitNodeName](path, state, this)
              }
            } 

          }

        }

      })

      pluginObject.visitor = visitorObject

      return pluginObject

    }

  }

}

export { Plugin }

export * from "camel-case";
export * from "capital-case";
export * from "constant-case";
export * from "dot-case";
export * from "header-case";
export * from "no-case";
export * from "param-case";
export * from "pascal-case";
export * from "path-case";
export * from "sentence-case";
export * from "snake-case";

import {
  pascalCase,
  pascalCaseTransform,
  pascalCaseTransformMerge,
  Options,
} from "pascal-case";

export { Options };

export function camelCaseTransform(input: string, index: number) {
  if (index === 0) return input.toLowerCase();
  return pascalCaseTransform(input, index);
}

export function camelCaseTransformMerge(input: string, index: number) {
  if (index === 0) return input.toLowerCase();
  return pascalCaseTransformMerge(input);
}

export function camelCase(input: string, options: Options = {}) {
  return pascalCase(input, {
    transform: camelCaseTransform,
    ...options,
  });
}

import { noCase, Options } from "no-case";

export { Options };

export function pascalCaseTransform(input: string, index: number) {
  const firstChar = input.charAt(0);
  const lowerChars = input.substr(1).toLowerCase();
  if (index > 0 && firstChar >= "0" && firstChar <= "9") {
    return `_${firstChar}${lowerChars}`;
  }
  return `${firstChar.toUpperCase()}${lowerChars}`;
}

export function pascalCaseTransformMerge(input: string) {
  return input.charAt(0).toUpperCase() + input.slice(1).toLowerCase();
}

export function pascalCase(input: string, options: Options = {}) {
  return noCase(input, {
    delimiter: "",
    transform: pascalCaseTransform,
    ...options,
  });
}

import { lowerCase } from "lower-case";

export interface Options {
  splitRegexp?: RegExp | RegExp[];
  stripRegexp?: RegExp | RegExp[];
  delimiter?: string;
  transform?: (part: string, index: number, parts: string[]) => string;
}

// Support camel case ("camelCase" -> "camel Case" and "CAMELCase" -> "CAMEL Case").
const DEFAULT_SPLIT_REGEXP = [/([a-z0-9])([A-Z])/g, /([A-Z])([A-Z][a-z])/g];

// Remove all non-word characters.
const DEFAULT_STRIP_REGEXP = /[^A-Z0-9]+/gi;

/**
 * Normalize the string into something other libraries can manipulate easier.
 */
export function noCase(input: string, options: Options = {}) {
  const {
    splitRegexp = DEFAULT_SPLIT_REGEXP,
    stripRegexp = DEFAULT_STRIP_REGEXP,
    transform = lowerCase,
    delimiter = " ",
  } = options;

  let result = replace(
    replace(input, splitRegexp, "$1\0$2"),
    stripRegexp,
    "\0"
  );
  let start = 0;
  let end = result.length;

  // Trim the delimiter from around the output string.
  while (result.charAt(start) === "\0") start++;
  while (result.charAt(end - 1) === "\0") end--;

  // Transform each token independently.
  return result.slice(start, end).split("\0").map(transform).join(delimiter);
}

/**
 * Replace `re` in the input string with the replacement value.
 */
function replace(input: string, re: RegExp | RegExp[], value: string) {
  if (re instanceof RegExp) return input.replace(re, value);
  return re.reduce((input, re) => input.replace(re, value), input);
}

/**
 * Locale character mapping rules.
 */
interface Locale {
  regexp: RegExp;
  map: Record<string, string>;
}

/**
 * Source: ftp://ftp.unicode.org/Public/UCD/latest/ucd/SpecialCasing.txt
 */
const SUPPORTED_LOCALE: Record<string, Locale> = {
  tr: {
    regexp: /\u0130|\u0049|\u0049\u0307/g,
    map: {
      : "\u0069",
      I: "\u0131",
      I: "\u0069",
    },
  },
  az: {
    regexp: /\u0130/g,
    map: {
      : "\u0069",
      I: "\u0131",
      I: "\u0069",
    },
  },
  lt: {
    regexp: /\u0049|\u004A|\u012E|\u00CC|\u00CD|\u0128/g,
    map: {
      I: "\u0069\u0307",
      J: "\u006A\u0307",
      : "\u012F\u0307",
      : "\u0069\u0307\u0300",
      : "\u0069\u0307\u0301",
      : "\u0069\u0307\u0303",
    },
  },
};

/**
 * Localized lower case.
 */
export function localeLowerCase(str: string, locale: string) {
  const lang = SUPPORTED_LOCALE[locale.toLowerCase()];
  if (lang) return lowerCase(str.replace(lang.regexp, (m) => lang.map[m]));
  return lowerCase(str);
}

/**
 * Lower case as a function.
 */
export function lowerCase(str: string) {
  return str.toLowerCase();
}

import { noCase, Options } from "no-case";
import { upperCaseFirst } from "upper-case-first";

export { Options };

export function capitalCaseTransform(input: string) {
  return upperCaseFirst(input.toLowerCase());
}

export function capitalCase(input: string, options: Options = {}) {
  return noCase(input, {
    delimiter: " ",
    transform: capitalCaseTransform,
    ...options,
  });
}

/**
 * Upper case the first character of an input string.
 */
export function upperCaseFirst(input: string) {
  return input.charAt(0).toUpperCase() + input.substr(1);
}

import { noCase, Options } from "no-case";
import { upperCase } from "upper-case";

export { Options };

export function constantCase(input: string, options: Options = {}) {
  return noCase(input, {
    delimiter: "_",
    transform: upperCase,
    ...options,
  });
}

/**
 * Locale character mapping rules.
 */
interface Locale {
  regexp: RegExp;
  map: Record<string, string>;
}

/**
 * Source: ftp://ftp.unicode.org/Public/UCD/latest/ucd/SpecialCasing.txt
 */
const SUPPORTED_LOCALE: Record<string, Locale> = {
  tr: {
    regexp: /[\u0069]/g,
    map: {
      i: "\u0130",
    },
  },
  az: {
    regexp: /[\u0069]/g,
    map: {
      i: "\u0130",
    },
  },
  lt: {
    regexp: /[\u0069\u006A\u012F]\u0307|\u0069\u0307[\u0300\u0301\u0303]/g,
    map: {
      i: "\u0049",
      j: "\u004A",
      : "\u012E",
      i: "\u00CC",
      i: "\u00CD",
      i: "\u0128",
    },
  },
};

/**
 * Localized upper case.
 */
export function localeUpperCase(str: string, locale: string) {
  const lang = SUPPORTED_LOCALE[locale.toLowerCase()];
  if (lang) return upperCase(str.replace(lang.regexp, (m) => lang.map[m]));
  return upperCase(str);
}

/**
 * Upper case as a function.
 */
export function upperCase(str: string) {
  return str.toUpperCase();
}

import { noCase, Options } from "no-case";

export { Options };

export function dotCase(input: string, options: Options = {}) {
  return noCase(input, {
    delimiter: ".",
    ...options,
  });
}

import { capitalCase, Options } from "capital-case";

export { Options };

export function headerCase(input: string, options: Options = {}) {
  return capitalCase(input, {
    delimiter: "-",
    ...options,
  });
}

import { dotCase, Options } from "dot-case";

export { Options };

export function paramCase(input: string, options: Options = {}) {
  return dotCase(input, {
    delimiter: "-",
    ...options,
  });
}

import { dotCase, Options } from "dot-case";

export { Options };

export function pathCase(input: string, options: Options = {}) {
  return dotCase(input, {
    delimiter: "/",
    ...options,
  });
}

import { noCase, Options } from "no-case";
import { upperCaseFirst } from "upper-case-first";

export { Options };

export function sentenceCaseTransform(input: string, index: number) {
  const result = input.toLowerCase();
  if (index === 0) return upperCaseFirst(result);
  return result;
}

export function sentenceCase(input: string, options: Options = {}) {
  return noCase(input, {
    delimiter: " ",
    transform: sentenceCaseTransform,
    ...options,
  });
}

import { dotCase, Options } from "dot-case";

export { Options };

export function snakeCase(input: string, options: Options = {}) {
  return dotCase(input, {
    delimiter: "_",
    ...options,
  });
}

import Is from '@pwn/is'
import { Visitor as BaseVisitor } from '@virtualpatterns/mablung-babel-plugin/visitor'

class Visitor extends BaseVisitor {

  constructor(babel) {
    super(babel)
  }

  get nodeType() {
    return [ 'StringLiteral' ]
  }

  onStringLiteralNode(path, state) {
    // console.log(`Visitor.onStringLiteralNode('${path.node.name}', state)`)

    let option = state.opts
    let rule = option.rule

    rule.forEach((rule) => {

      rule.searchForPattern = rule.searchForPattern ? rule.searchForPattern : Is.regexp(rule.searchFor) ? rule.searchFor : new RegExp(rule.searchFor, 'gi')

      path.node.value = path.node.value.replace(rule.searchForPattern, rule.replaceWith)

    })

  }

}

export { Visitor }

// {
